CREATE DATABASE IF NOT EXISTS CityBot;
USE CityBot;
CREATE TABLE City(
	id INT NOT NULL AUTO_INCREMENT,
	name varchar(45) NOT NULL UNIQUE,
    description varchar(10000),
    	PRIMARY KEY(id)
);
